module.exports.hello = function()
{
	console.log("It's a small world.");
};

module.exports.playerConnect = function(data, callback)
{
	// Load player
	if(data.userID !== undefined)
	{
		callback( { action : "playerConnect", connected : true } );
	}
};

module.exports.playerDisconnect = function(data, callback)
{
	// Unload player
	if(data.userID !== undefined)
	{
		callback( { action : "playerDisconnect", connected : false } );
	}
};

module.exports.demoAction = function(data, callback)
{
	callback( { action : "demoAction", foo : true, bar: false } );
};